DROP TABLE IF EXISTS user_friends;
DROP TABLE IF EXISTS user;
CREATE TABLE user (UserID BIGINT NOT NULL AUTO_INCREMENT PRIMARY KEY, username VARCHAR(50) NOT NULL UNIQUE, email VARCHAR(100) NOT NULL UNIQUE, fullname VARCHAR(100) NOT NULL, addr VARCHAR(500) DEFAULT NULL, dob DATE DEFAULT NULL, img_path VARCHAR(255) DEFAULT NULL, rel_status ENUM('Single', 'Committed', 'Engaged', 'Married', 'Its Complicated') DEFAULT 'Single', loc VARCHAR(255) DEFAULT NULL, pass VARCHAR(255) NOT NULL);
CREATE TABLE user_friends (user_id BIGINT NOT NULL, friend_id BIGINT NOT NULL, accepted BOOLEAN DEFAULT FALSE, PRIMARY KEY (user_id, friend_id));